package If_Else;

public class Even_Odd_Numbers {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
	int number = 27;
	
	if (number%2 == 0)
	{
		System.out.println("Bumber is even number");
	}
	
	else
	{
	System.out.println("Number is odd number");
	}
	
		
		
	}

}
