package Loops;

public class Multiplication_Practice {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int n=2;
		int a=3;
		int c=4;
		int e=5;
		int g=6;
		int j=7;
		int l=8;
		int o=9;
		
		
		
		for (int i=1;i<=10;i++)
		{
			System.out.println(n + "X" +i+"=" +(n*i));
		}
			
		for (int b=1;b<=10;b++)
		{
			System.out.println(a + "X" +b+"=" +(a*b));
		}	
		
		for (int d=1;d<=10;d++)
		{
			System.out.println(c + "X" +d+"=" +(c*d));
		}	
		
		
		for (int f=1;f<=10;f++)
		{
			System.out.println(e + "X" +f+"=" +(e*f));
		}	
		
		for (int h=1;h<=10;h++)
		{
			System.out.println(g + "X" +h+"=" +(g*h));
		}	
		
		for (int k=1;k<=10;k++)
		{
			System.out.println(j + "X" +k+"=" +(j*k));
		}	
		
		for (int m=1;m<=10;m++)
		{
			System.out.println(l + "X" +m+"=" +(l*m));
		}	
		
		for (int p=1;p<=10;p++)
		{
			System.out.println(o + "X" +p+"=" +(o*p));
		}	
		
		
		
		
		
		
	}

}
